const bill=document.querySelector("#bill"),custom=document.querySelector("#custom"),people=document.querySelector("#peope");let theValue=0;const btns=document.querySelectorAll(".calc-count__tip-btn"),reset=document.querySelector(".calc-output__btn"),tipAPerson=document.getElementById("tipPerPerson"),totalAmount=document.getElementById("totalEach");function tipCalculator(){tip=theValue/100*+bill.value,total=+bill.value+tip,tipPerPerson=(tip/+people.value).toFixed(2),totalPerPerson=(total/+people.value).toFixed(2),tipAPerson.innerHTML="$"+tipPerPerson,totalAmount.innerHTML="$"+totalPerPerson}btns.forEach(((e,t)=>{e.addEventListener("click",(()=>{custom.value="",theValue=e.value,tipCalculator(),btns.forEach(((e,l)=>{t===l?e.classList.add("active"):e.classList.remove("active")}))}))})),bill.addEventListener("keyup",(()=>{tipCalculator()})),people.addEventListener("keyup",(()=>{""!=bill.value|null!=bill.value&&theValue&&tipCalculator()})),custom.addEventListener("keyup",(()=>{theValue=custom.value,btns.forEach((e=>{e.classList.remove("active")})),tipCalculator()})),reset.addEventListener("click",(()=>{theValue="",bill.value="",custom.value="",people.value="",btns.forEach((e=>{e.classList.remove("active")})),tipAPerson.innerHTML="$0.00",totalAmount.innerHTML="$0.00"}));